/// <summary>
///    Validates invoice document date.
/// </summary>
/// <param name="_transDate">
///    The transaction date to check.
/// </param>
/// <param name="_documentDate">
///    The document date to check.
/// </param>
/// <returns>
///    true if the validations is successful; otherwise, false.
/// </returns>
public static boolean checkDocumentDate(TransDate _transDate, DocumentDate _documentDate)
{
    boolean ok = true;
    VendInvoiceDocDate vendInvoiceDocDate = VendParameters::find().VendInvoiceDocDate;

    if (vendInvoiceDocDate != VendInvoiceDocDate::None)
    {
        if (_documentDate > _transDate)
        {
            switch (vendInvoiceDocDate)
            {
                case VendInvoiceDocDate::Warning:
                    warning("@SYS95923");
                    break;

                case VendInvoiceDocDate::Error:
                    ok = checkFailed("@SYS95923");
                    break;
            }
        }
        else
        {
            if (!_documentDate)
            {
                switch (vendInvoiceDocDate)
                {
                    case VendInvoiceDocDate::Warning:
                        warning("@SYS95922");
                        break;

                    case VendInvoiceDocDate::Error:
                        ok = checkFailed("@SYS95922");
                        break;
                }
            }
        }
    }

    return ok;
}

static boolean exist()
{
    return (select firstonly RecId from vendParameters).RecId != 0;
}

static VendParameters find(boolean _forupdate = false)
{
    VendParameters parameter;

    parameter.selectForUpdate(_forupdate);

    select firstonly parameter
        index Key
        where parameter.Key == 0;

    if (!parameter && !parameter.isTmp())
    {
        Company::createParameter(parameter);
        PriceDiscPurchPolicyParameters::initParameters();
    }

    return parameter;
}

public static VendParameters findByCompany(DataAreaId _company, boolean _forUpdate = false)
{
    VendParameters parameter;

    changecompany(_company)
    {
        parameter = VendParameters::find(_forUpdate);
    }

    return parameter;
}

    /// <summary>
    /// Checks if the automatic submission to workflow is enabled in at least one company.
    /// </summary>
    /// <returns>True, if automatic submission to workflow is enabled in at least one company; false, otherwise.</returns>
    internal static boolean isAutomationWorkflowSubmissionEnabledInAnyCompany()
    {
        VendParameters parameter;

	    select firstonly crosscompany RecId from parameter
            where parameter.Key == 0 && parameter.AutomationWorkflowSubmission ==  NoYes::Yes;

	    return (parameter.RecId != 0);
    }

/// <summary>
///    Determines what the internal default group value should be based on the parameters defined in the
///    <c>VendParameters</c> table.
/// </summary>
/// <returns>
///    The <c>VendInvoiceGrouping</c> default internal group value.
/// </returns>
public static VendInvoiceGrouping getDefaultInternalInvoiceGroup()
{
    xSession session = new xSession();
    VendParameters vendParameters = VendParameters::find();
    VendInvoiceGrouping defaultGroup;

    //Only return a group if the AP Invoice Group feature is enabled
    if (vendParameters.VendInvoiceGroupingEnable == NoYes::No)
    {
        return "";
    }
    else
    {
        if (vendParameters.VendInvoiceDefaultGroupType == VendInvoiceDefaultGroupType::Date)
        {
            defaultGroup = date2str(DateTimeUtil::getSystemDate(DateTimeUtil::getUserPreferredTimeZone()), 213, 2, 3, 2, 3, 4, DateFlags::None);
        }
        else if (vendParameters.VendInvoiceDefaultGroupType == VendInvoiceDefaultGroupType::Username)
        {
            defaultGroup = session.userId();
        }
        else
        {
            defaultGroup = vendParameters.VendInvoiceInternalDefaultGroup;
        }

        return defaultGroup;
    }
}

