/// <summary>
///    Validates whether the AP Invoice Grouping feature can be disabled based on the existing invoices.
/// </summary>
/// <returns>
///    true if the feature can be disabled; otherwise, false.
/// </returns>
public static boolean validateVendInvoiceGroupsCanBeDisabled()
{
    VendInvoiceInfoTable vendInvoiceInfoTable;

    //look for open, unposted invoices with an invoice group in PurchParmTable
    select firstonly RecId from vendInvoiceInfoTable
            where (vendInvoiceInfoTable.ParmJobStatus == ParmJobStatus::Waiting || vendInvoiceInfoTable.ParmJobStatus == ParmJobStatus::ContainErrors) &&
                  (vendInvoiceInfoTable.Ordering == DocumentStatus::Invoice || vendInvoiceInfoTable.Ordering == DocumentStatus::ApproveJournal) &&
                  (vendInvoiceInfoTable.VendInvoiceGroup != '');

    //if there are invoices that are open and have an invoice group assigned, then the feature cannot be disabled.
    if (vendInvoiceInfoTable.RecId != 0)
    {
        return false;
    }

    //otherwise the feature can be disabled.
    return true;
}

