internal edit PositiveNumber editMaxInvoicesPerBatch(boolean set, PositiveNumber _maxInvoicesPerBatch)
{
    if (set)
    {
        this.MaxInvoicesPerBatch = _maxInvoicesPerBatch;
    }
    else
    {
        _maxInvoicesPerBatch = VendParameters::maxInvoicesPerBatch();
    }

    return _maxInvoicesPerBatch;
}

internal edit PositiveNumber editMaxWaitTimeForInvoiceScheduledStatus_Hours(boolean set, PositiveNumber _maxWaitTimeForInvoiceScheduledStatus)
{
    if (set)
    {
        this.MaxWaitTimeForInvoiceScheduledStatus_Hours = _maxWaitTimeForInvoiceScheduledStatus;
    }
    else
    {
        _maxWaitTimeForInvoiceScheduledStatus = VendParameters::maxWaitTimeForInvoiceScheduledStatus();
    }

    return _maxWaitTimeForInvoiceScheduledStatus;
}

internal edit PositiveNumber editPollingIntervalMinutes(boolean set, PositiveNumber _pollingIntervalMinutes)
{
    if (set)
    {
        this.PollingInterval_Minutes = _pollingIntervalMinutes;
        this.updateSeriesInterval();
    }
    else
    {
        _pollingIntervalMinutes = VendParameters::pollingIntervalMinutes();
    }

    return _pollingIntervalMinutes;
}

internal static PositiveNumber maxInvoicesPerBatch()
{
    PositiveNumber maxInvoices = VendParameters::find().MaxInvoicesPerBatch;

    return maxInvoices == 0 ? VendInvoicePostTaskConstants::MaxInvoicesPerBatch : maxInvoices;
}

internal static PositiveNumber maxWaitTimeForInvoiceScheduledStatus()
{
    PositiveNumber maxWaitTime = VendParameters::find().MaxWaitTimeForInvoiceScheduledStatus_Hours;

    return maxWaitTime == 0 ? VendInvoicePostTaskConstants::MaxWaitTimeForInvoiceScheduledStatus_Hours : maxWaitTime;
}

internal static PositiveNumber pollingIntervalMinutes()
{
    PositiveNumber pollingIntervalMinutes = VendParameters::find().PollingInterval_Minutes;

    return pollingIntervalMinutes == 0 ? VendInvoicePostTaskConstants::PollingInterval_Minutes : pollingIntervalMinutes;
}

private void updateSeriesInterval()
{
    ProcessScheduleSeriesPollingDetails processScheduleSeriesPollingDetails = ProcessScheduleSeries::getPollingDetailsForSeries(
        VendInvoicePostTaskConstants::VendorInvoiceBatchPosting,
        "@AccountsPayable:VendInvoicePostTaskFeatureLabel");

    if (processScheduleSeriesPollingDetails && processScheduleSeriesPollingDetails.parmInterval() != this.PollingInterval_Minutes)
    {
        processScheduleSeriesPollingDetails.parmInterval(this.PollingInterval_Minutes);

        ProcessScheduleSeries::setPollingDetailsForSeries(
            VendInvoicePostTaskConstants::VendorInvoiceBatchPosting,
            "@AccountsPayable:VendInvoicePostTaskFeatureLabel",
            processScheduleSeriesPollingDetails);
    }
}

/// <summary>
/// Returns the maximum number of invoices an automatic workflow submission process
/// is allowed to process on any given run.
/// </summary>
/// <returns>The maximum number of invoices.</returns>
internal static PositiveNumber getMaximumInvoicesAllowedForAutomatedWorkflowSubmission()
{
    VendParameters parameters = VendParameters::find();

    if (parameters.VendInvoiceAutomationWorkflowSubmissionLimit <= 0)
    {
        ttsbegin;
        parameters.selectForUpdate(true);
        parameters.VendInvoiceAutomationWorkflowSubmissionLimit = VendInvoiceAutomationConstants::DefaultMaximumInvoicesAllowedForAutomatedWorkflowSubmission;
        parameters.update();
        ttscommit;
    }

    return parameters.VendInvoiceAutomationWorkflowSubmissionLimit;
}

