/// <summary>
/// Info message when user selects automatic workflow submission but there are no active vendor invoice workflow setup for the company in setup page for vendor invoice automation feature.
/// </summary>
/// <returns>True, if there is an active vendor invoice workflow configuration for company. If not, return false.</returns>
internal static boolean validateActiveVendInvoiceWorkflowPerCompany()
{
    boolean doesActiveWFConfigExists = true;
    WorkflowTable workflowTable;
    WorkflowVersionTable versionTable;
    WorkflowTypeName workflowTemplateName = workFlowTypeStr(VendProcessInvoice);
    SysDictWorkflowType sysDictWorkflowTemplate = new SysDictWorkflowType(workflowTemplateName);

    switch (sysDictWorkflowTemplate.associationType())
    {
        case WorkflowAssociationType::Company:
            select workflowTable
                exists join versionTable
                    where versionTable.Enabled == NoYes::Yes
                        && versionTable.WorkflowTable == workflowTable.RecId
                        && workflowTable.Type == WorkflowConfigurationType::Definition
                        && workflowTable.DataArea == curext()
                        && workflowTable.TemplateName == workflowTemplateName;
            break;
        case WorkflowAssociationType::Global:
            select workflowTable
                exists join versionTable
                    where versionTable.Enabled == NoYes::Yes
                        && versionTable.WorkflowTable == workflowTable.RecId
                        && workflowTable.Type == WorkflowConfigurationType::Definition
                        && workflowTable.TemplateName == workflowTemplateName;
            break;
    }

    if (!workflowTable)
    {
        doesActiveWFConfigExists = false;
    }

    return doesActiveWFConfigExists;
}

