}

/// <summary>
/// Sets default value of the field VendInvoicePostingDateAutoAdjustment to "ChangeToFirstDayOfNewPeriodWhenPeriodIsOnHoldOrClosed" in all the companies    
/// </summary>
internal static void setVendInvoicePostingDateAutoAdjustmentDefaultValue()
{
    DataArea dataArea;

    ttsbegin;

    while select dataArea where dataArea.isVirtual == NoYes::No
    {
        changecompany(dataArea.id)
        {
            VendParameters vendParameters = VendParameters::find(true);
            vendParameters.VendInvoicePostingDateAutoAdjustment = VendInvoicePostingDateAutoAdjustment::ChangeToFirstDayOfNewPeriodWhenPeriodIsOnHoldOrClosed;
            vendParameters.update();
        }
    }

    ttscommit;
}
