Method.Source:
edit VendExportFormat actionClassName(boolean _set, VendExportFormat _vendExportFormat)
{
    VendPaymFormat vendPaymFormat;
    // <GBR>
    ClassId classIdCur = this.ClassId;
    // </GBR>

    if (_set)
    {
        vendPaymFormat = VendPaymFormat::findFormat(_vendExportFormat, BankFormat::Export);
        if (vendPaymFormat)
        {
            this.ClassId = vendPaymFormat.ClassId;
        }
        else
        {
            this.ClassId = 0;
        }
    }
    else
    {
        vendPaymFormat = VendPaymFormat::find(this.ClassId, BankFormat::Export);
        if (this.ClassId && !vendPaymFormat)
        {
            this.ClassId = 0;
        }
    }

    // <GBR>
    if (BrazilParameters::isEnabled()
        && this.ClassId == classNum(VendOutPaym_CNAB_BR)
        && this.PaymSumBy != PaymSumBy::Invoice)
    {
        error(strfmt("@GLS1167", "@SYS14598", "@SYS12128", VendPaymFormat::find(this.ClassId, BankFormat::Export).Format));
        this.ClassId = classIdCur;
    }
    // </GBR>
    return vendPaymFormat.Format;
}

