/// <summary>
/// Validates Period selected
/// </summary>
/// <returns>
/// Returns false with error message if Period is other than Invoice else returns true.
/// </returns>
public boolean checkPeriod_BR()
{
    boolean isValid = true;

    if (BrazilParameters::isEnabled())
    {
        if (this.ClassId == classNum(VendOutPaym_CNAB_BR)
            && this.PaymSumBy != PaymSumBy::Invoice)
        {
            isValid = checkFailed(strfmt(
                "@GLS1167",
                "@SYS14598",
                "@SYS12128",
                VendPaymFormat::find(this.ClassId, BankFormat::Export).Format));
        }
    }

    return isValid;
}

public void insert()
{
    super();

    this.IsabelpaymentReference = VendpaymentReference::Message;
}

public void modifiedField(FieldId _fieldId)
{
    super(_fieldId);

    switch (_fieldId)
    {
        case fieldNum(VendPaymModeTable, AccountType):
            this.parmPaymentLedgerDimension(0);
            if (this.AccountType != LedgerJournalACType::Bank)
            {
                this.BankTransType = '';
            }
            break;

        case fieldNum(VendPaymModeTable, FurtherPosting):
            if (!this.FurtherPosting)
            {
                this.InterCompanyLedgerDimension = 0;
                this.pdcClearingPosting = NoYes::No;
                this.BridgingAccountByBank = NoYes::No;
            }
            break;
        case fieldNum(VendPaymModeTable, PaymOnInvoice):
            if (this.PaymOnInvoice == NoYes::No)
            {
                this.ExportOnInvoice = NoYes::No;
            }
            break;

        case fieldNum(VendPaymModeTable, pdcClearingPosting):
            if (this.pdcClearingPosting == NoYes::Yes)
            {
                this.FurtherPosting = NoYes::Yes;
                if (!FeatureStateProvider::isFeatureEnabled(BankBridgedTransAutoClearingFeature::instance()) || this.BridgingAccountByBank == NoYes::No)
                {
                    this.InterCompanyLedgerDimension = BankParameters::find().IssueClearingPDC;
                }
            }
            break;

        case fieldNum(VendPaymModeTable, BridgingAccountByBank):
            if (this.BridgingAccountByBank == NoYes::Yes)
            {
                this.FurtherPosting = NoYes::Yes;
                this.InterCompanyLedgerDimension = 0;
                this.AccountType = LedgerJournalACType::Bank;
            }
            break;

        // <GBR>
        case fieldNum(VendPaymModeTable, PrimaryMethod_BR):
            if (this.PrimaryMethod_BR != PrimaryMethod_BR::Other)
            {
                this.DescPrimaryMethod_BR = '';
            }
            break;
        // </GBR>
        case fieldNum(VendPaymModeTable, PaymentType):
            if (this.PaymentType != this.getPaymentTypeElectronicPayment())
            {
                this.DirectDebit = NoYes::No;
            }
            break;
    }
}

