/// <summary>
/// Validates brazilian fields of <c>VendPaymMode</c> table
/// </summary>
/// <param name="_fieldId">
/// Field identifier
/// </param>
/// <returns>
/// True if brazilian fields is valid otherwise false
/// </returns>
public boolean postValidateField_BR(FieldId _fieldId)
{
    boolean ret = true;

    switch (_fieldId)
    {
        case fieldNum(VendPaymModeTable, Posting_BR):
        case fieldNum(VendPaymModeTable, AccountType):
            if (this.Posting_BR != Posting_BR::None && this.AccountType != LedgerJournalACType::Bank)
            {
                ret = checkFailed("@GLS665");
            }
            break;

        case fieldnum(VendPaymModeTable, PaymSumBy),
            fieldnum(VendPaymModeTable, ReturnLayoutGroupId_BR),
            fieldnum(VendPaymModeTable, ClassId),
            fieldnum(VendPaymModeTable, ExportLayoutGroupId_BR):
            ret = this.checkPeriod_BR();
            break;
        case fieldNum(VendPaymModeTable, PaymentMethod_BR):
            if (this.PaymentMethod_BR == EFDocPaymentMethod_BR::TradeCard)
            {
                ret = checkFailed(strFmt("@NFE:InvalidValueForNFE4", this.PaymentMethod_BR));
            }
            break;
    }

    return ret;
}

/// <summary>
/// Validates record post validateWrite method
/// </summary>
/// <returns>
/// Returns false with error message if validation fails else returns true.
/// </returns>
public boolean postValidateWrite_BR()
{
    boolean ret = true;

    ret = this.validateExportFormat_BR() && ret;
    ret = this.validateReturnFormat_BR() && ret;
    ret = this.validateDescPrimaryMethod_BR() && ret;
    ret = this.validateVendPaymMode_BR() && ret;
    ret = this.validatePaymentAccount_BR() && ret;
    ret = this.validateLayoutGroups_BR() && ret;

    return ret;
}

private boolean validateDescPrimaryMethod_BR()
{
    if (this.PrimaryMethod_BR == PrimaryMethod_BR::Other
        && !this.DescPrimaryMethod_BR)
    {
        return checkFailed(strFmt(
            "@GLS50957",
            fieldPName(CustPaymModeTable, PrimaryMethod_BR),
            enum2Value(PrimaryMethod_BR::Other),
            fieldPName(CustPaymModeTable, DescPrimaryMethod_BR)));
    }

    return true;
}

private boolean validateExportFormat_BR()
{
    boolean ret = true;

    if (this.UseGERConfiguration == NoYes::No)
    {
        if (this.ClassId == classNum(VendOutPaym_CNAB_BR) && this.checkPaymentTypeForElectronicPayment())
        {
            ret = true;
        }
        else if (this.ClassId == classNum(VendOutPaym_CNAB_BR) || this.checkPaymentTypeForElectronicPayment())
        {
            ret = checkFailed(strFmt("@SYS4000747", enum2str(PaymentType::ElectronicPayment), new VendOutPaym_CNAB_BR().interfaceName()));
        }
    }

    return ret;
}

/// <summary>
///     Checks for the payment type on <c>VendPaymmodetable</c>.
/// </summary>
/// <returns>
///     true if payment type is electronic payment; otherwise, false.
/// </returns>
protected boolean checkPaymentTypeForElectronicPayment()
{
    return this.PaymentType == PaymentType::ElectronicPayment;
}

public boolean validateField(fieldId _fieldIdToCheck)
{
    boolean isValid = super(_fieldIdToCheck);

    if (isValid)
    {
        switch (_fieldIdToCheck)
        {
            case fieldNum(VendPaymModeTable, PaymentLedgerDimension):
                if (this.AccountType == LedgerJournalACType::Bank)
                {
                    if (this.parmPaymAccount())
                    {
                        isValid = BankAccountTable::checkIsActive(this.parmPaymAccount());
                    }
                }
                break;
        }
    }

    // <GBR>
    if (BrazilParameters::isEnabled())
    {
        isValid = this.postValidateField_BR(_fieldIdToCheck) && isValid;
    }
    // </GBR>

    return isValid;
}

private boolean validateLayoutGroups_BR()
{
    boolean ret = true;

    if (this.checkPaymentTypeForElectronicPayment() && this.UseGERConfiguration == NoYes::No)
    {
        if (!this.ExportLayoutGroupId_BR)
        {
            ret = checkFailed(strfmt("@SYS26332", fieldpname(VendPaymModeTable, ExportLayoutGroupId_BR)));
        }
        if (!this.ReturnLayoutGroupId_BR)
        {
            ret = checkFailed(strfmt("@SYS26332", fieldpname(VendPaymModeTable, ReturnLayoutGroupId_BR)));
        }
    }

    return ret;
}

private boolean validatePaymentAccount_BR()
{
    if (this.Posting_BR != Posting_BR::None)
    {
        if (this.PaymOnInvoice && !this.PaymentLedgerDimension)
        {
            return checkFailed("@GLS160285");
        }
    }

    return true;
}

private boolean validateReturnFormat_BR()
{
    boolean ret = true;

    if (this.UseGERConfiguration == NoYes::No)
    {
        if (this.ClassIdReturn == classNum(VendPaymReconcilImport_EP_Return_BR) && this.checkPaymentTypeForElectronicPayment())
        {
            ret = true;
        }
        else if (this.ClassIdReturn == classNum(VendPaymReconcilImport_EP_Return_BR) || this.checkPaymentTypeForElectronicPayment())
        {
            ret = checkFailed(strFmt("@SYS4000810", enum2str(PaymentType::ElectronicPayment), new VendPaymReconcilImport_EP_Return_BR().interfaceName()));
        }
    }

    return ret;
}

private boolean validateVendPaymMode_BR()
{
    boolean ret = true;

    switch (this.ClassId)
    {
        case classNum(VendOutPaym_Cheque):
            if (this.Posting_BR == Posting_BR::None)
            {
                ret = checkFailed(strFmt("@GLS1169", "@GLS1175", "@SYS7439", "@SYS8079", "@SYS22495"));
            }
            if (this.PaymSumBy != PaymSumBy::Total)
            {
                ret = checkFailed(strFmt("@GLS1168", "@SYS14598", "@SYS9242", "@SYS22495"));
            }
            break;

        default:
            if (this.Posting_BR != Posting_BR::None)
            {
                ret = checkFailed(strFmt("@GLS1204", "@SYS1369", "@SYS22495", "@GLS666"));
            }
            break;
    }

    return ret;
}

public boolean validateWrite()
{
    boolean ret = super();

    this.checkIfERFormatsChosen();

    // <GBR>
    if (BrazilParameters::isEnabled())
    {
        ret = this.postValidateWrite_BR() && ret;
    }
    // </GBR>

    return ret;
}

public void write()
{
    if (this.ClassId != this.orig().ClassId)
    {
        VendPaymModeSpec vendPaymModeSpec;
        delete_from vendPaymModeSpec
            where vendPaymModeSpec.PaymMode == this.PaymMode;
    }
    super();
}

public static boolean checkExist(PaymMode _paymMode)
{
    boolean ret = true;

    if (!VendPaymModeTable::exist(_paymMode))
    {
        ret = checkFailed(strFmt(VendPaymModeTable::txtNotExist(), _paymMode));
    }

    return ret;
}

public static DiscGraceDays discGraceDays(PaymMode _paymMode)
{
    VendPaymModeTable vendPaymModeTable;

    if (_paymMode)
    {
        select firstonly DiscGraceDays from vendPaymModeTable
            index hint PaymModeIdx
            where vendPaymModeTable.PaymMode == _paymMode;
    }
    return vendPaymModeTable.DiscGraceDays;
}

public static boolean exist(PaymMode _paymMode)
{
    return (select firstonly RecId from VendPaymModeTable
        where VendPaymModeTable.PaymMode == _paymMode).RecId != 0;
}

public static VendPaymModeTable find(
    PaymMode _paymMode,
    boolean _forupdate = false,
    ConcurrencyModel _concurrencyModel = ConcurrencyModel::Auto)
{
    VendPaymModeTable vendPaymModeTable;

    vendPaymModeTable.selectForUpdate(_forupdate);
    if (_forupdate && _concurrencyModel != ConcurrencyModel::Auto)
    {
        vendPaymModeTable.concurrencyModel(_concurrencyModel);
    }

    select firstonly vendPaymModeTable
        where vendPaymModeTable.PaymMode == _paymMode;

    return vendPaymModeTable;
}

public static LedgerDimensionDefaultAccount findInterCompanyLedgerDimension(PaymMode _paymMode)
{
    return VendPaymModeTable::find(_paymMode).InterCompanyLedgerDimension;
}

public static void lookupPaymMethod(FormStringControl _stringControl, str _filterStr, CompanyId _company)
{
    CompanyId company= _company;

    if (company == '')
    {
        company = curext();
    }

    changecompany(company)
    {
        Args e = new Args();
        e.name(formStr(VendPaymModeTableLookup));
        e.lookupField(fieldNum(VendPaymModeTable, PaymMode));
        e.lookupValue(_filterStr);
        e.caller(_stringControl);

        FormRun formRun = classfactory.formRunClass(e);
        formRun.init();

        _stringControl.performFormLookup(formRun);
    }
}

/// <summary>
/// Creates a default dimension based on the specified values and the values from the specified payment
/// mode.
/// </summary>
/// <param name="_paymMode">
/// A record in the <c>PaymMode</c> table from which to get the default settings.
/// </param>
/// <param name="_defaultDimension">
/// The record ID of a record in the <c>DimensionAttributeValueSetItem</c> table that represents the
/// vendor transaction default dimension.
/// </param>
/// <param name="_curDefaultDimension">
/// The record ID of a record in the <c>DimensionAttributeValueSetItem</c> table that represents the
/// current default dimension.
/// </param>
/// <returns>
/// The record ID of a record in the <c>DimensionAttributeValueSetItem</c> table that represents the
/// calculated default dimension.
/// </returns>
public static DimensionDefault setDefaultDimension(PaymMode _paymMode, DimensionDefault _defaultDimension, DimensionDefault _curDefaultDimension)
{
    DimensionDefault defaultDimension;
    if (CustVendPaymProposalKeepDefaultDimensionNOFlight::instance().isEnabled())
    {
        defaultDimension = _defaultDimension ? _defaultDimension : _curDefaultDimension;
    }
    else
    {
        defaultDimension = _defaultDimension;
    }

    VendPaymModeTable vendPaymModeTable = VendPaymModeTable::find(_paymMode);

    if (vendPaymModeTable.DimCtrl == NoYes::Yes)
    {
        DimensionAttributeValueSetItemView setItemView;
        DimensionAttributeSetItem dimUse;

        DimensionAttributeValue dimAttrValue;
        DimensionAttributeValueSetStorage valueSetStorage = new DimensionAttributeValueSetStorage();

        if (!CustVendPaymProposalKeepDefaultDimensionNOFlight::instance().isEnabled())
        {
            defaultDimension = _defaultDimension ? _defaultDimension : _curDefaultDimension;
        }

        // Evaluate each value in the incoming default dimension and add to the set if specified
        while select DimensionAttribute, DimensionAttributeValue from setItemView
            where setItemView.DimensionAttributeValueSet == defaultDimension
        join RecId from dimUse
            where dimUse.DimensionAttribute == setItemView.DimensionAttribute
                && dimUse.DimensionAttributeSet == vendPaymModeTable.DimensionAttributeSet
                && dimUse.EnumerationValue == NoYes::Yes
        join HashKey, EntityInstance from dimAttrValue
            where dimAttrValue.RecId == setItemView.DimensionAttributeValue
        {
            valueSetStorage.addItemValues(setItemView.DimensionAttribute, setItemView.DimensionAttributeValue, dimAttrValue.HashKey, dimAttrValue.EntityInstance);
        }

        defaultDimension = valueSetStorage.save();
    }
    else if (!CustVendPaymProposalKeepDefaultDimensionNOFlight::instance().isEnabled()
        && SysCountryRegionCode::isLegalEntityInCountryRegion([#isoNO])
        && SkipLedgerDimensionInPaymentProposalNOFlight::instance().isEnabled())
    {
        defaultDimension = 0;
    }
    return defaultDimension;
}

