private void checkIfERFormatsChosen()
{
    if (this.UseGERConfiguration == NoYes::Yes && !this.ERFormatMappingID)
    {
        warning(strFmt("@AccountsPayable:ExportFormatConfigurationNotSet", this.PaymMode));
    }

    if (this.UseGERConfigurationForReturn == NoYes::Yes
        && !this.ERReturnConfiguration && !this.ERReturnConfigurationSecondary)
    {
        warning(strFmt("@AccountsPayable:ReturnFormatConfigurationNotSet", this.PaymMode));
    }
}
