/// <summary>
/// Returns a dimension set that holds the merged combination of the current <c>defaultDimension</c>
/// field on this table and the specified dimension set.
/// </summary>
/// <param name="_primaryDefaultDimension">
/// The first dimension set to merge with the current <c>defaultDimension</c> field on this table.
/// </param>
/// <param name="_secondaryDefaultDimension">
/// The second dimension set to merge with the current <c>defaultDimension</c> field on this table;
/// optional.
/// </param>
/// <param name="_dimensionMerge">
/// A <c>dimensionMerge</c> object that was previously instantiated by using the current buffer;
/// optional.
/// </param>
/// <returns>
/// A dimension set that holds the merged combination of the current <c>defaultDimension</c> field on
/// this table and the provided dimension set.
/// </returns>
/// <remarks>
/// This method makes sure that potential linked dimensions are not overwritten when merging.
/// Additionally, the entity specifier defaulting will be considered.
/// </remarks>

public DimensionDefault mergeDimension(
    DimensionDefault _primaryDefaultDimension,
    DimensionDefault _secondaryDefaultDimension = 0,
    DimensionMerge   _dimensionMerge = DimensionMerge::newFromTable(this,
                                                                    this.companyInfo().RecId
                                                                    )
    )
{
    return _dimensionMerge.merge(_primaryDefaultDimension, _secondaryDefaultDimension);
}

/// <summary>
///  Prints one or more RFQ journals.
/// </summary>
/// <param name="_purchRFQJournalPrint">
///   A <c>PurchRFQJournalPrint</c> object; optional.
/// </param>
/// <param name="_journalList">
///   A <c>RecordSortedList</c> object containing journals to be printed; optional.
/// </param>
/// <remarks>
///   All printing logic has been moved from the <c>PurchRFQFormLetter</c> class to the <c>PurchRFQJournalPrint</c> class, hence an instance of <c>PurchRFQJournalPrint</c> can be passed in.
/// </remarks>
void  printJournal(PurchRFQJournalPrint _purchRFQJournalPrint = null,
                   RecordSortedList     _journalList     = null)
{
    Args            parameters = new Args();
    MenuFunction    purchRFQSendMenu;

    purchRFQSendMenu = new MenuFunction(menuitemOutputStr(PurchRFQ),MenuItemType::Output);

    parameters.caller(_purchRFQJournalPrint);

    if (_journalList)
        parameters.object(_journalList);
    else
        parameters.record(this);

    purchRFQSendMenu.run(parameters);
}

