static boolean exist(PurchRFQId rfqId)
{
    return (select firstonly RecId from vendRFQJour
                index hint RFQIdx
                where vendRFQJour.rfqId == rfqId).RecId != 0;
}

public static VendRFQJour find(PurchRFQId     rfqId,
                               boolean        _forUpdate = false)

{
    VendRFQJour  vendRFQJour;

    if (rfqId)
    {
        vendRFQJour.selectForUpdate  (_forUpdate);

        select firstonly vendRFQJour
            index hint RFQIdx
            where vendRFQJour.rfqId == rfqId;
    }

    return vendRFQJour;
}

public void insert()
{
    super();

    VendRFQAmendmentJour::copyAmendmentLines(this.RecId, PurchRFQCaseTable::find(this.RFQCaseId).RecId);
}
