static boolean  exist(PurchRFQId    rfqId,
                      LineNum       lineNum = naReal())
{
    if (rfqId)
    {
        if (lineNum == naReal())
            return (select firstonly RecId from vendRFQTrans
                        index PurchRFQLineIdx
                        where vendRFQTrans.rfqId     == rfqId
                   ).RecId     != 0;

        return (select firstonly RecId from vendRFQTrans
                        index PurchRFQLineIdx
                        where vendRFQTrans.rfqId       == rfqId     &&
                              vendRFQTrans.LineNum     == lineNum
                   ).RecId != 0;
    }

    return false;
}

static VendRFQTrans find(PurchRFQId     rfqId,
                         LineNum        lineNum     = naReal(),
                         boolean        update      = false)
{
    VendRFQTrans    vendRFQTrans;

    if (rfqId)
    {
        vendRFQTrans.selectForUpdate(update);

        if (lineNum == naReal())
        {
            select firstonly vendRFQTrans
                index hint PurchRFQLineIdx
                where vendRFQTrans.rfqId == rfqId;
        }
        else
        {
            select firstonly vendRFQTrans
                index hint PurchRFQLineIdx
                where vendRFQTrans.rfqId   == rfqId    &&
                      vendRFQTrans.LineNum == lineNum;
        }
    }

    return vendRFQTrans;
}

/// <summary>
/// Gets the vendRFQJour.
/// </summary>
/// <param name="_forUpdate">
///    A boolean value that determines whether to select the record for update; optional.
/// </param>
/// <returns>
/// The vendRFQJour record.
/// </returns>
VendRFQJour vendRFQJour(boolean _forUpdate = false)
{
    VendRFQJour vendRFQJour;

    vendRFQJour.selectForUpdate(_forUpdate);

    select firstonly vendRFQJour
        index hint RFQIdx
        where vendRFQJour.RFQId             == this.RFQId
            && vendRFQJour.InternalRFQId    == this.InternalRFQId
            && vendRFQJour.RFQDate          == this.RFQDate;

    return vendRFQJour;
}
