	/// <summary>
	/// Display method of the name of the account owner.
	/// </summary>
    /// <returns>
    /// Returns <c>Name</c> object.
    /// </returns>
    display Name displayAccountName()
    {
        Name        ret;

        if ((select firstonly whsJourLoadLine where whsJourLoadLine.LoadId == this.LoadId).InventTransType == InventTransType::Sales)
        {
            ret = CustTable::find(this.AccountNum).name();
        }
        else if ((select firstonly whsJourLoadLine where whsJourLoadLine.LoadId == this.LoadId).InventTransType == InventTransType::Purch)
        {
            ret = VendTable::find(this.AccountNum).name();
        }

        return ret;
    }

	/// <summary>
	/// Display method if the appointment id of the load.
	/// </summary>
    /// <returns>
    /// Returns <c>TMSApptId</c> object.
    /// </returns>
    display TMSApptId displayAppointmentId()
    {
        return TMSAppointment::displayByLoadId(this.LoadId).ApptId;
    }

	/// <summary>
	/// Display method for the number of load lines.
	/// </summary>
    /// <returns>
    /// Returns <c>RefRecId</c> object.
    /// </returns>
    display RefRecId displayLoadLines()
    {
        return (select count(RecId) from whsJourLoadLine
                    where whsJourLoadLine.LoadId    == this.LoadId  &&
                          whsJourLoadLine.RefId     == this.RefId).RecId;
    }

	/// <summary>
	/// Display method for the number of shipments made for the load.
	/// </summary>
    /// <returns>
    /// Returns <c>RecId</c> object.
    /// </returns>
    display RecId displayNumberOfShipments()
    {
        return (select count(RecId) from whsJourLoadLine
                    group by ShipmentId
                    where whsJourLoadLine.LoadId        == this.LoadId      &&
                          whsJourLoadLine.RefId         == this.RefId       &&
                          whsJourLoadLine.ShipmentId    != '').RecId;
    }

	/// <summary>
	/// Display method for the total quantity of the load.
	/// </summary>
    /// <returns>
    /// Returns <c>ProductQuantity</c> object.
    /// </returns>
    display ProductQuantity displayTotalQty()
    {
        return (select sum(Qty) from whsJourLoadLine
                    where whsJourLoadLine.LoadId    == this.LoadId  &&
                          whsJourLoadLine.RefId     == this.RefId).Qty;
    }

/// <summary>
/// This method displays the total weight of the load.
/// </summary>
/// <returns>
/// The total weight of the load.
/// </returns>
[SysClientCacheDataMethodAttribute(true)]
public display Weight displayTotalWeight()
{
    Weight              weight;
    WHSJourLoadLine     jourloadLine;

    while select jourloadLine
        where jourloadLine.LoadId    == this.LoadId   &&
              jourloadLine.RefId        == this.RefId
    {
        weight += jourloadLine.displayItemWeight();
    }

    return weight;
}

/// <summary>
/// This method displays the Wave Id value.
/// </summary>
/// <returns>
/// The Wave Id.
/// </returns>
[SysClientCacheDataMethodAttribute(true)]
public display WHSWaveId displayWave()
{
    WHSShipmentTable    shipmentTable;
    WHSWaveId           ret;
    int                 i;

    while select WaveId from shipmentTable
        group by WaveId
        where shipmentTable.LoadId == this.LoadId
    {
        ++i;

        ret = shipmentTable.WaveId;

        if (i > 1)
        {
            break;
        }
    }

    return (i == 1 ? ret : '');
}

	/// <summary>
	/// Display method for the remaining weight left in the load.
	/// </summary>
    /// <returns>
    /// Returns <c>Weight</c> object.
    /// </returns>
    display Weight displayWeightRemaining()
    {
        Weight          ret;

        ret = WHSLoadTemplate::find(this.LoadTemplateId).LoadMaxWeight - this.displayTotalWeight();

        return ret;
    }

