	/// <summary>
	/// Display method for the total charged price for the load.
	/// </summary>
    /// <returns>
    /// Returns <c>Price</c> object.
    /// </returns>
    display Price totalLoadPrice()
    {
        WHSJourLoadLine     jourLoadLine;
        CustConfirmTrans    custConfirmTrans;
        CustInvoiceTrans    custInvoiceTrans;
        Qty                 qty;
        Price               salesPrice;
        Amount              ret;

        if (this.CustJourType == WHSCustJourType::Confirmation)
        {
            while select ItemId, Qty, UOM, InventDimId from jourLoadLine
                where jourLoadLine.LoadId   == this.LoadId
                join ItemId, Qty, DiscAmount, DiscPercent, SalesPrice from custConfirmTrans
                    where custConfirmTrans.ConfirmId        == this.RefId
                    &&    custConfirmTrans.InventTransId    == jourLoadLine.InventTransId
            {
                salesPrice = custConfirmTrans.SalesPrice;

                qty = InventTableModule::unitConvert(jourLoadLine.ItemId,
                                                     ModuleInventPurchSales::Invent,
                                                     jourLoadLine.UOM,
                                                     jourLoadLine.Qty,
                                                     jourLoadLine.InventDimId);

                ret += (qty * salesPrice)  - (((salesPrice - custConfirmTrans.DiscAmount) * (custConfirmTrans.DiscPercent / 100)) * qty) - (qty * custConfirmTrans.DiscAmount);
            }
        }

        else
        {
            while select ItemId, Qty, UOM, InventDimId from jourLoadLine
                where jourLoadLine.ParmId   == this.ParmId 
                &&    jourLoadLine.LoadId   == this.LoadId
                join ItemId,Qty,DiscAmount,DiscPercent,SalesPrice from custInvoiceTrans
                    where custInvoiceTrans.InvoiceId        == this.RefId
                    &&    custInvoiceTrans.InventTransId    == jourLoadLine.InventTransId
            {
                salesPrice = custInvoiceTrans.SalesPrice;

                qty = InventTableModule::unitConvert(jourLoadLine.ItemId, 
                                                     ModuleInventPurchSales::Invent, 
                                                     jourLoadLine.UOM, 
                                                     jourLoadLine.Qty,
                                                     jourLoadLine.InventDimId);

                ret += (qty * salesPrice)  - (((salesPrice - custInvoiceTrans.DiscAmount) * (custInvoiceTrans.DiscPercent / 100)) * qty) - (qty * custInvoiceTrans.DiscAmount);
            }
        }

        return ret;
    }

	/// <summary>
	/// Checks whether a specific <c>whsJourLoadTable</c> record exists.
	/// </summary>
	/// <param name="_loadId">
	/// Load id of the record.
	/// </param>
	/// <returns>
	/// True if the record exists.
	/// </returns>
    static boolean exist(WHSLoadId _loadId)
    {
        return _loadId && (select firstonly RecId from whsJourLoadTable
                 where whsJourLoadTable.LoadId == _loadId).RecId != 0;
    }

/// <summary>
/// Determines whether the specified record in the <c>WHSJourLoadTable</c> table exists.
/// </summary>
/// <param name="_parmId">
/// The parm ID of the load.
/// </param>
/// <param name="_refId">
/// The reference ID of the load.
/// </param>
/// <returns>
/// true if the specified record exists; otherwise, false.
/// </returns>
static boolean existByParmId(
    ParmId              _parmId,
    WHSCustJourRefId    _refId)
{
    return _parmId
        &&  _refId
        && (select firstonly RecId from whsJourLoadTable
                where whsJourLoadTable.ParmId == _parmId
                   && whsJourLoadTable.RefId  == _refId).RecId != 0;
}

	/// <summary>
	/// Finds a specific <c>whsJourLoadTable</c> record with given parameters.
	/// </summary>
	/// <param name="_loadId">
	/// Load id of the record.
	/// </param>
	/// <param name="_refId">
	/// Journal reference id of the record.
	/// </param>
	/// <param name="_parmId">
	/// Parm id of the record.
	/// </param>
	/// <param name="_forupdate">
	/// Determines whether the record is selected for update.
	/// </param>
    /// <param name="_custPackingSlipJourRecId">
    /// The parameter of type <c>RefRecId</c>.
    /// </param>
	/// <returns>
	/// The record that is searched for.
	/// </returns>
public static WHSJourLoadTable find(WHSLoadId           _loadId,
                                        WHSCustJourRefId    _refId,
                                        ParmId              _parmId,
                                        boolean             _forupdate = false,
                                        RefRecId            _custPackingSlipJourRecId = 0)
    {
        WHSJourLoadTable    jourLoadTable;

        if (_loadId &&
            _refId  &&
            _parmId)
        {
            jourLoadTable.selectForUpdate(_forupdate);

            select firstonly jourLoadTable
                where jourLoadTable.LoadId      == _loadId  &&
                      jourLoadTable.RefId       == _refId   &&
                      jourLoadTable.ParmId      == _parmId  &&
                      (!_custPackingSlipJourRecId
                       || jourLoadTable.CustPackingSlipJourRecId == _custPackingSlipJourRecId);
        }

        return jourLoadTable;
    }

/// <summary>
/// Records the load information for the order being updated.
/// </summary>
/// <param name="_salesId">
/// ID of the sales order the journal is updated for.
/// </param>
/// <param name="_journalId">
/// ID of the journal updated.
/// </param>
/// <param name="_jourType">
/// Type of the journal udpated.
/// </param>
/// <param name="_parmId">
/// ID of the journal update operation.
/// </param>
/// <param name="_proforma">
/// true if handling a pro forma document; otherwise, false.
/// </param>
/// <remarks>
/// Will short-circuit, if the WHSandTMS configuration key is turned off.
/// </remarks>
public static void insertJourLoadTable(
    SalesId          _salesId,
    WHSCustJourRefId _journalId,
    WHSCustJourType  _jourType,
    ParmId           _parmId,
    boolean          _proforma = false
)
{
    WHSJourLoadTable                whsJourLoadTable;
    WHSLoadTableCustPackingSlipJour whsLoadTableCustPackingSlipJour;
    WHSLoadTable                    loadTable;
    WHSLoadLine                     loadLine;
    RecordInsertList                recordInsertList = new RecordInsertList(tableNum(WHSJourLoadTable));

    if (!WHSParameters::find().EnableOrderLoadHistory)
    {
        return;
    }

    if (_jourType == WHSCustJourType::Confirmation)
    {
        // only non-delivered loads for confirm
        while select loadTable
            exists join loadLine
                where loadLine.LoadId           ==  loadTable.LoadId
                   && loadLine.OrderNum         == _salesId
                   && loadLine.InventTransType  == InventTransType::Sales
                   && loadLine.TransportRequest == NoYes::No
            notexists join whsLoadTableCustPackingSlipJour
                where whsLoadTableCustPackingSlipJour.whsLoadTableLoadId     == loadLine.LoadId
                   && whsLoadTableCustPackingSlipJour.whsLoadTableDataAreaId == loadLine.DataAreaId
        {
            whsJourLoadTable.RefId = _journalId;
            whsJourLoadTable.ParmId = _parmId;
            whsJourLoadTable.CustJourType = _jourType;

            whsJourLoadTable.initFromWHSLoadTable(loadTable);

            recordInsertList.add(whsJourLoadTable);
        }
        recordInsertList.insertDatabase();
    }

    if (_jourType == WHSCustJourType::Invoice)
    {
        while select loadTable
            join CustPackingSlipJourRecId from whsLoadTableCustPackingSlipJour
                where whsLoadTableCustPackingSlipJour.whsLoadTableLoadId     == loadTable.LoadId
                   && whsLoadTableCustPackingSlipJour.whsLoadTableDataAreaId == loadTable.DataAreaId
            exists join loadLine
                where loadLine.LoadId           ==  loadTable.LoadId
                   && loadLine.OrderNum         == _salesId
                   && loadLine.InventTransType  == InventTransType::Sales
                   && loadLine.TransportRequest == NoYes::No
        {
            if (whsLoadTableCustPackingSlipJour.CustPackingSlipJourRecId  || _proforma)
            {
                whsJourLoadTable.RefId = _journalId;
                whsJourLoadTable.ParmId = _parmId;
                whsJourLoadTable.CustJourType = _jourType;

                whsJourLoadTable.initFromWHSLoadTable(loadTable);

                whsJourLoadTable.CustPackingSlipJourRecId = whsLoadTableCustPackingSlipJour.CustPackingSlipJourRecId;

                recordInsertList.add(whsJourLoadTable);
            }
        }
        recordInsertList.insertDatabase();
    }
}

/// <summary>
/// Determines if concurrent deletes should throw exception.
/// </summary>
/// <returns>true if exception should be thrown; otherwise false.</returns>
[Hookable(false)]
public boolean shouldThrowExceptionOnZeroDelete()
{
    return true;
}
