	/// <summary>
	/// Checks whether a specific <c>whsLocationType</c> record exists.
	/// </summary>
	/// <param name="_whsLocType">
	/// Location type of the record.
	/// </param>
	/// <returns>
	/// True if the record exists.
	/// </returns>
    public static boolean exist(WHSLocType _whsLocType)
    {
        return (_whsLocType) &&
               (select firstonly RecId from whsLocationType
                where whsLocationType.LocType == _whsLocType).RecId != 0;
    }

	/// <summary>
	/// Finds a specific <c>whsLocationType</c> record with given parameters.
	/// </summary>
	/// <param name="_locType">
	/// Location type of the record.
	/// </param>
	/// <param name="_forupdate">
	/// Determines whether the record is selected for update.
	/// </param>
	/// <returns>
	/// The record that is searched for.
	/// </returns>
    public static WHSLocationType find(WHSLocType _locType,
                                       boolean    _forupdate = false)
    {
        WHSLocationType    locationType;

        if (_locType)
        {
            locationType.selectForUpdate(_forupdate);

            select firstonly locationType
                where locationType.LocType == _locType;
        }

        return locationType;
    }

