    if (this.isFieldVisibleMenuItemDirectedBy())
    {
        switch (this.MenuItemDirectedBy)
        {
            case WHSMenuItemDirectedBy::CycleCountGrouping:
                return false;
            default:
                return !this.handleByLicensePlateActive();
        }
    }
    return false;
}

