	/// <summary>
	/// Checks if a specific <c>WHSRequestType</c> record exists.
	/// </summary>
	/// <param name="_whsRequestTypeId">
	/// Request type id of the record.
	/// </param>
	/// <returns>
	/// True if the record exists.
	/// </returns>
        public static boolean exist(WHSRequestTypeId _whsRequestTypeId)
    {
        return (_whsRequestTypeId) &&
               (select firstonly WHSRequestType
                where whsRequestType.RequestTypeId == _whsRequestTypeId).RecId != 0;
    }

	/// <summary>
	/// Finds a specific <c>WHSRequestType</c> record with given parameters.
	/// </summary>
	/// <param name="_whsRequestTypeId">
	/// Request type id of the record.
	/// </param>
	/// <param name="_forupdate">
	/// Determines whether the record is selected for update.
	/// </param>
	/// <returns>
	/// The record that is searched for.
	/// </returns>
        public static WHSRequestType find(WHSRequestTypeId _whsRequestTypeId,
                                      boolean          _forupdate = false)
    {
        WHSRequestType    whsRequestType;

        if (_whsRequestTypeId)
        {
            whsRequestType.selectForUpdate(_forupdate);

            select firstonly whsRequestType
                where whsRequestType.RequestTypeId == _whsRequestTypeId;
        }

        return whsRequestType;
    }

/// <summary>
