public void initValue()
{
    super();

    this.SequenceNumber = WHSSystemDirectedWorkSequenceQuery::getNextSequenceNumber(this.MenuItemName);
    this.PackedQueryRun = this.defaultPackedQueryRun().pack();
}

/// <summary>
/// Returns the default query run.
/// </summary>
/// <returns>The default query run.</returns>
[Hookable(false)]
internal QueryRun defaultPackedQueryRun()
{
    Query query = new Query(queryStr(WHSWorkSysDir));        
    // Add a default LockedUser desc sort order to retain legacy sorting behavior.
    QueryBuildDataSource qbdsWorkTable = query.dataSourceTable(tableNum(WHSWorkTable));
    qbdsWorkTable.addOrderByField(fieldNum(WHSWorkTable, LockedUser), SortOrder::Descending);


    return new QueryRun(query);
}

	/// <summary>
    /// Returns the next sequence number in for a specific <c>WHSMenuItemName</c>.
    /// </summary>
    /// <param name = "_menuItemName">The menu item name for finding the next <c>WHSSeqNum</c>.</param>
    /// <returns>The next <c>WHSSeqNum</c> for the provided <c>WHSMenuItemName</c>.</returns>
	private static WHSSeqNum getNextSequenceNumber(WHSMenuItemName _menuItemName)
    {
        WHSSystemDirectedWorkSequenceQuery systemDirectedWorkSequenceQuery;

        select maxOf(SequenceNumber) from systemDirectedWorkSequenceQuery
            where systemDirectedWorkSequenceQuery.MenuItemName == _menuItemName;

        return (systemDirectedWorkSequenceQuery.SequenceNumber + 1);
    }

[Hookable(false)]
internal static void createDefaultWorkSequenceQuery(WHSMenuItemName _menuItemName)
{
    WHSSystemDirectedWorkSequenceQuery systemDirectedWorkSequenceQuery = WHSSystemDirectedWorkSequenceQuery::find(_menuItemName, 1, false);

    if (!systemDirectedWorkSequenceQuery)
    {
        systemDirectedWorkSequenceQuery.MenuItemName     = _menuItemName;
        systemDirectedWorkSequenceQuery.initValue();
        systemDirectedWorkSequenceQuery.QueryDescription = "@WAX:WHSSystemDirectedSequenceEnablement_AutoGeneratedQuery";
        systemDirectedWorkSequenceQuery.PackedQueryRun   = systemDirectedWorkSequenceQuery.defaultPackedQueryRun().pack();

        systemDirectedWorkSequenceQuery.insert();
    }
}

/// <summary>
/// Finds a specific <c>WHSSystemDirectedWorkSequenceQuery</c> record with given parameters.
/// </summary>
/// <param name="_menuItemName">
/// Menu item name of the record.
/// </param>
/// <param name="_seqNum">
/// The sequence number of the record.
/// </param>
/// <param name="_forUpdate">
/// Determines whether the record is selected for update.
/// </param>
/// <returns>
/// The record that is searched for.
/// </returns>
public static WHSSystemDirectedWorkSequenceQuery find(WHSMenuItemName _menuItemName, WHSSeqNum _seqNum, boolean _forUpdate)
{
    WHSSystemDirectedWorkSequenceQuery systemDirectedWorkSequenceQuery;

    if (_menuItemName && _seqNum)
    {
        systemDirectedWorkSequenceQuery.selectForUpdate(_forUpdate);

        select firstOnly systemDirectedWorkSequenceQuery
            where systemDirectedWorkSequenceQuery.MenuItemName		== _menuItemName
                && systemDirectedWorkSequenceQuery.SequenceNumber	== _seqNum;
    }

    return systemDirectedWorkSequenceQuery;
}

/// <summary>
/// Determines if concurrent deletes should throw exception.
/// </summary>
/// <returns>true if exception should be thrown; otherwise false.</returns>
[Hookable(false)]
public boolean shouldThrowExceptionOnZeroDelete()
{
    return true;
}
