Method.Source:
    public boolean validateWrite()
{
    boolean ret;

    ret = super();

    // Is update of Axapta record not allowed
    if (this.OutlookSyncDirections != smmOutlookSyncDirections::None && this.AllowedSyncDirections != smmOutlookSyncDirections::FullSynchronization)
    {
        if (this.AllowedSyncDirections != this.OutlookSyncDirections)
        {
            // This synchronization direction is not allowed
            ret = ret && checkFailed("@SYS90090");

            this.OutlookSyncDirections = this.AllowedSyncDirections;
        }
    }

    return ret;
}

