    public static void createDefaultSetup()
{
    smmAxaptaOutlookMapping mapField;

    ttsbegin;

    delete_from mapField;

    smmAxaptaOutlookMapping::createContactSetup();

    smmAxaptaOutlookMapping::createAppointmentSetup();

    smmAxaptaOutlookMapping::createTaskSetup();

    ttscommit;
}

