/// <summary>
/// Gets the <c>smmOutlookSyncDirection</c> value that is based on the specified contact field.
/// </summary>
/// <param name="_contactField">
/// An <c>smmOutlookContactFields</c> enumeration value.
/// </param>
/// <returns>
/// An <c>smmOutlookSyncDirections</c> enumeration value.
/// </returns>
public static smmOutlookSyncDirections getContactSyncDirection(smmOutlookContactFields _contactField)
{
    smmAxaptaOutlookMapping outlookMapping;

    select OutlookSyncDirections
        from outlookMapping where outlookMapping.OutlookContactField == _contactField && outlookMapping.OutlookMappingType == smmOutlookMappingTypes::Contacts;

    return outlookMapping.OutlookSyncDirections;
}

    static boolean isOutlookMappingSetupCompleted()
{
    smmAxaptaOutlookMapping smmAxaptaOutlookMapping;

    select firstonly RecId from smmAxaptaOutlookMapping;

    return smmAxaptaOutlookMapping.RecId != 0;
}

/// <summary>
/// Determines if concurrent deletes should throw exception.
/// </summary>
/// <returns>true if exception should be thrown; otherwise false.</returns>
public boolean shouldThrowExceptionOnZeroDelete()
{
    return super() || SalesEnforceConcurrentDeletesFlight::instance().isEnabled();
}
