public void initValue()
{
    const int daysInYear = 365;
    const int defaultCampaignExpiryDate = 21;
    const int defaultCampaignFollowupDays = 3;

    super();

    // Set primary key. The value 1 has to be used because to legacy CRM data
    this.Key = 1;

    // Business relation
    this.LanguageId         = CompanyInfo::languageId();
    this.CurrencyCode       = CompanyInfoHelper::standardCurrency();

    this.BusRelOpenCustForm = NoYes::Yes;
    this.OpenFromTime       = str2time('08:00:00');
    this.OpenToTime         = str2time('16:00:00');

    // Documents
    this.DocuAgeLimitForDisplay = daysInYear;

    // Quotation
    this.QuotationActivityPurpose   = "@SYS81379"; // Quotation expires
    this.QuotationActivityCategory  = smmActivityCategory::Task;

    // Transaction Log
    this.LogCustTable      = NoYes::No;
    this.LogCustTrans      = NoYes::No;
    this.LogVendTable      = NoYes::No;
    this.LogVendTrans      = NoYes::No;
    this.LogContactPerson  = NoYes::No;
    this.LogActivities     = NoYes::No;
    this.LogDocuRef        = NoYes::No;
    this.LogSalesTable     = NoYes::No;
    this.LogQuotationTable = NoYes::No;
    this.LogMailings       = NoYes::No;
    this.LogBusRel         = NoYes::No;
    this.LogCampaignTable  = NoYes::No;
    this.LogOpportunity    = NoYes::No;
    this.LogLeadTable      = NoYes::No;
    this.LogCaseDetail     = NoYes::No;
    this.LogTMCallListTable = NoYes::No;
    this.TransLogInterval  = daysInYear;
    this.LogInsert         = NoYes::Yes;
    this.LogDelete         = NoYes::Yes;
    this.LogUpdate         = smmTransLogUpdateAction::Last;

    // Campaign
    this.CampaignActivityPurpose        = "@SYS80362"; // campaign end
    this.CampaignNumOfDaysExpiryDate    = defaultCampaignExpiryDate;
    this.NumOfCampaignDaysFollowUpDate  = defaultCampaignFollowupDays;
    this.CampaignActivityCategory       = smmActivityCategory::Task;

    // Telemarketing
    this.tmCallBackActivityPurpose = "@SYS80361"; // Telemarketing call back
    this.tmFollowUpActivityPurpose = "@SYS79884"; // Telemarketing

    // Opportunity
    this.CreateOpportunityForSalesQuotation = smmCreateOpportunityOptions::Prompt;
    this.CreateOpportunityForProjectQuotation = smmCreateOpportunityOptions::Prompt;
    this.UpdateOpportunityForSalesQuotation = smmUpdateOpportunityOptions::Prompt;
    this.UpdateOpportunityForProjectQuotation = smmUpdateOpportunityOptions::Prompt;
}

public container getDefaultingDependencies()
{
    return [
            [fieldNum(smmParametersTable, PeriodFrom),
                [
                    fieldNum(smmParametersTable, PeriodId)
                ]
            ],
            [fieldNum(smmParametersTable, PeriodTo),
                [
                    fieldNum(smmParametersTable, PeriodId)
                ]
            ]
        ];
}

public void defaultField(FieldId _fieldId)
{
    super(_fieldId);

    switch (_fieldId)
    {
        case fieldNum(smmParametersTable, PeriodFrom):
            this.defaultPeriodFrom();
            break;

        case fieldNum(smmParametersTable, PeriodTo):
            this.defaultPeriodTo();
            break;
    }
}

private void defaultPeriodFrom()
{
    if (this.isFieldSet(fieldNum(smmParametersTable, PeriodId)))
    {
        this.PeriodFrom = dateNull();
    }
}

private void defaultPeriodTo()
{
    if (this.isFieldSet(fieldNum(smmParametersTable, PeriodId)))
    {
        this.PeriodTo = dateNull();
    }
}

public boolean validateField(FieldId _fieldIdToCheck)
{
    boolean ret;

    ret = super(_fieldIdToCheck);

    if (ret)
    {
        switch (_fieldIdToCheck)
        {
            case fieldNum(smmParametersTable, CallBackCreateActivity):
                if (this.CallBackCreateActivity == smmCreateActivity::Yes || this.CallBackCreateActivity == smmCreateActivity::Prompt)
                {
                    //Create activity for call back must not be Yes or Prompt.
                    ret = checkFailed("@SYS90482");
                }
                break;

            case fieldNum(smmParametersTable, tmCallBackActivityCategory):
                if (this.tmCallBackActivityCategory == smmActivityCategory::Appointment)
                {
                    //Call back activity category must not be Apointment.
                    ret = checkFailed("@SYS90483");
                }
                break;

            case fieldNum(smmParametersTable, TelemarketingTargetActivityCategory):
                if (this.TelemarketingTargetActivityCategory == smmActivityCategory::Appointment)
                {
                    //Telemarketing target category must not be Apointment.
                    ret = checkFailed("@SYS90484");
                }
                break;

            case fieldNum(smmParametersTable, CampaignTargetActivityCategory):
                if (this.CampaignTargetActivityCategory == smmActivityCategory::Appointment)
                {
                    //Campaign category must not be Apointment.
                    ret = checkFailed("@SYS90485");
                }
                break;

            case fieldNum(smmParametersTable, NumOfCampaignDaysFollowUpDate):
            case fieldNum(smmParametersTable, CampaignNumOfDaysExpiryDate):
                if (this.CampaignNumOfDaysExpiryDate < this.NumOfCampaignDaysFollowUpDate)
                {
                    //Expiry days must be greater than Follow-up days.
                    ret = checkFailed("@SYS91356");
                }
                break;

            case fieldNum(smmParametersTable, PeriodFrom):
            case fieldNum(smmParametersTable, PeriodTo):
                if (this.PeriodFrom && this.PeriodTo && this.PeriodFrom > this.PeriodTo)
                {
                    // From date must be before To date
                    ret = checkFailed("@SYS91020");
                }
                break;

            case fieldNum(smmParametersTable, OpenFromTime):
            case fieldNum(smmParametersTable, OpenToTime):
                // Check that open to time is not before open from time
                if (this.OpenFromTime > this.OpenToTime)
                {
                    // The Closing time you have selected is before the Opening time.
                    warning("@SYS99916");
                }
                break;
        }
    }

    return ret;
}

public boolean validateWrite()
{
    boolean ret;

    ret = super();

    // Check that the document type id specified for files is of the file type
    if (this.DocuTypeIdForFiles && DocuType::find(this.DocuTypeIdForFiles).TypeGroup != DocuTypeGroup::File)
    {
        // The document type for files must be of the type File
        ret = ret && checkFailed("@SYS81367");
    }

    // Check that the document type id specified for incoming email is of the file type
    if (this.DocuTypeIdForInEMail && DocuType::find(this.DocuTypeIdForInEMail).TypeGroup != DocuTypeGroup::File)
    {
        // The document type for email in must be of the type File
        ret = ret && checkFailed("@SYS81368");
    }

    // Check that the document type id specified for outgoing email is of the file type
    if (this.DocuTypeIdForOutEMail && DocuType::find(this.DocuTypeIdForOutEMail).TypeGroup != DocuTypeGroup::File)
    {
        // The document type for email out must be of the type File
        ret = ret && checkFailed("@SYS81369");
    }

    return ret;
}

public static smmParametersTable find(boolean _forupdate = false)
{
    smmParametersTable parametersTable = null;

    if (parametersTable.isTmp())
    {
        return parametersTable;
    }

    parametersTable.selectForUpdate(_forupdate);

    // Find paramters table
    select firstonly parametersTable index key where parametersTable.Key == 1;

    // If paramters table is not found it is created
    if (!parametersTable && !parametersTable.isTmp())
    {
        // Use the generic framework for creating the parameters table
        Company::createParameter(parametersTable);
    }

    return parametersTable;
}

