/// <summary>
///     Gets the document title of the related agreement.
/// </summary>
/// <returns>
///     The agreement document title.
/// </returns>
[SysClientCacheDataMethodAttribute(true)]
public display AgreementDocumentTitle agreementDocumentTitle_RU()
{
    return PurchAgreementHeader::find(this.MatchingAgreement).DocumentTitle;
}

/// <summary>
///     Initializes a buffer of the view based on a temporary proforma version.
/// </summary>
/// <param name="_purchTableVersion">
///     The temporary version to initialize from.
/// </param>
public void initFromProformaPurchTableVersion(PurchTableVersion _purchTableVersion)
{
    PurchTable          purchTable;
    PurchTableHistory   purchTableHistory;
    utcDateTime         versionDateTime;

    if (_purchTableVersion.Proforma == NoYes::No)
    {
        throw error(error::wrongUseOfFunction(funcName()));
    }

    this.initFromPurchTableVersion(_purchTableVersion);

    if (_purchTableVersion.IsArchived == NoYes::Yes)
    {
        versionDateTime = _purchTableVersion.VersionDateTime;
        select firstonly validTimeState(versionDateTime) * from purchTableHistory
            where   purchTableHistory.PurchId       == _purchTableVersion.PurchId
            &&      purchTableHistory.dataAreaId    == _purchTableVersion.PurchIdDataAreaId;
        this.initFromPurchTableHistory(purchTableHistory);
    }
    else
    {
        select firstonly * from purchTable
            where   purchTable.PurchId       == _purchTableVersion.PurchId
            &&      purchTable.dataAreaId    == _purchTableVersion.PurchIdDataAreaId;
        this.initFromPurchTable(purchTable);
    }
}

/// <summary>
///     Initializes a view buffer based on the specified purchase order record.
/// </summary>
/// <param name="_purchTable">
///     A record from the <c>PurchTable</c> table to initialize from.
/// </param>
protected void initFromPurchTable(PurchTable _purchTable)
{
    PurchTable_RU   ruPurchTable    = _purchTable.purchTable_RU();

    this.initFromPurchTableMap(_purchTable);
    this.InventProfileId_RU         =    ruPurchTable.InventProfileId_RU;
    this.VATChargeSource_RU         =    ruPurchTable.VATChargeSource_RU;
    this.VATOperationCode_RU        =    ruPurchTable.VATOperationCode_RU;
    this.InventProfileType_RU       =    ruPurchTable.InventProfileType_RU;
    this.ConsigneeAccount_RU        =    ruPurchTable.ConsigneeAccount_RU;
    this.ConsignorAccount_RU        =    ruPurchTable.ConsignorAccount_RU;
    this.PriceAgreementDate_RU      =    ruPurchTable.PriceAgreementDate_RU;
    this.AgreementHeaderExt_RU      =    ruPurchTable.AgreementHeaderExt_RU;
}

