/// <summary>
///     Initializes a view buffer based on the specified purchase order version record.
/// </summary>
/// <param name="_purchTableVersion">
///     A record from the <c>PurchTableVersion</c> table to initialize from.
/// </param>
private void initFromPurchTableVersion(PurchTableVersion _purchTableVersion)
{
    this.PurchId                    = _purchTableVersion.PurchId;
    this.VersionDateTime            = _purchTableVersion.VersionDateTime;
    this.PurchTableVersionRecId     = _purchTableVersion.RecId;
    this.IsArchived                 = _purchTableVersion.IsArchived;
    this.Proforma                   = _purchTableVersion.Proforma;
}

/// <summary>
/// Indicates whether the current version is the original.
/// </summary>
/// <returns>
/// true if this is the original; otherwise, false.
/// </returns>
public boolean isFirstVersion()
{
    boolean                    firstVersion = true;
    PurchTableHistory          firstPurchTableHistory;

    firstPurchTableHistory = PurchTableHistory::findFirst(this.PurchId);
    if (firstPurchTableHistory.RecId
        && firstPurchTableHistory.ValidFrom != this.VersionDateTime)
    {
        firstVersion = false;
    }

    return firstVersion;
}

public SourceDocumentHeader parmSourceDocumentHeader()
{
    SourceDocumentHeader sourceDocumentHeader;

    sourceDocumentHeader = SourceDocumentHeader::find(this.SourceDocumentHeader);

    return sourceDocumentHeader;
}
